import { useCallback, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";

interface AudioUploaderProps {
  onFileSelect: (file: File) => void;
  onSampleTest: () => void;
  selectedFile: File | null;
  onClearFile: () => void;
  disabled?: boolean;
}

export function AudioUploader({ onFileSelect, onSampleTest, selectedFile, onClearFile, disabled }: AudioUploaderProps) {
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileSelect = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file
    const maxSize = 50 * 1024 * 1024; // 50MB
    const allowedExtensions = ['.wav', '.mp3', '.flac', '.ogg', '.m4a'];
    const fileExtension = file.name.toLowerCase().substring(file.name.lastIndexOf('.'));

    if (file.size > maxSize) {
      alert('æ–‡ä»¶éå¤§ï¼Œè«‹é¸æ“‡å°æ–¼50MBçš„æ–‡ä»¶');
      return;
    }

    if (!allowedExtensions.includes(fileExtension)) {
      alert('ä¸æ”¯æ´çš„æ–‡ä»¶æ ¼å¼ï¼Œè«‹ä½¿ç”¨WAV/MP3/FLAC/OGG/M4Aæ ¼å¼');
      return;
    }

    onFileSelect(file);
  }, [onFileSelect]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.add('dragover');
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('dragover');
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('dragover');

    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
