import { useState, useCallback } from "react";
import { AudioSeparationAPI } from "@/services/audioSeparationAPI";
import type { AudioTask, UploadResponse, HealthResponse } from "@shared/schema";

export function useAudioSeparation(baseURL = 'https://d5c4ba628261.ngrok-free.app') {
  const [api] = useState(() => new AudioSeparationAPI(baseURL));
  const [isProcessing, setIsProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState('');
  const [result, setResult] = useState<AudioTask | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [currentTaskId, setCurrentTaskId] = useState<string | null>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [originalAudioBlob, setOriginalAudioBlob] = useState<Blob | null>(null);
  const [connectionStatus, setConnectionStatus] = useState<'checking' | 'connected' | 'disconnected'>('checking');
  const [healthData, setHealthData] = useState<HealthResponse | null>(null);

  const checkHealth = useCallback(async (): Promise<boolean> => {
    try {
      setConnectionStatus('checking');
      const health = await api.checkHealth();
      setHealthData(health);
      setConnectionStatus(health.status === 'ok' ? 'connected' : 'disconnected');
      return health.status === 'ok';
    } catch (error) {
      // å¥åº·æª¢æŸ¥å¤±æ•—æ˜¯æ­£å¸¸ç¾è±¡ï¼ˆä¾‹å¦‚ngrokç¶²å€å¤±æ•ˆï¼‰ï¼Œä¸éœ€è¦è¨˜éŒ„ç‚ºéŒ¯èª¤
      setHealthData(null);
      setConnectionStatus('disconnected');

      // é€šçŸ¥å¾Œç«¯è¨˜éŒ„é€£ç·šå¤±æ•—ï¼Œå•Ÿå‹•ç›£æ§å’Œéƒµä»¶æé†’
      try {
        await fetch('/api/monitor/connection-error', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ 
            apiUrl: api.baseURL, 
            error: error instanceof Error ? error.message : 'é€£æ¥å¤±æ•—' 
          })
        });
      } catch (notifyError) {
        console.error('ç„¡æ³•è¨˜éŒ„é€£ç·šéŒ¯èª¤:', notifyError);
      }

      return false;
    }
  }, [api]);

  const processAudio = useCallback(async (audioFile: File) => {
    try {
      setIsProcessing(true);
      setProgress(0);
      setStatus('ä¸Šå‚³ä¸­...');
      setError(null);
      setResult(null);

      // Store original audio blob for playback
      setOriginalAudioBlob(audioFile);

      // Check health first
      const isHealthy = await checkHealth();
      if (!isHealthy) {
        throw new Error('å¾Œç«¯æœå‹™ä¸å¯ç”¨ï¼Œè«‹ç¨å¾Œå†è©¦');
      }

      // Upload file
      const uploadResult: UploadResponse = await api.uploadAudio(audioFile);
      setCurrentTaskId(uploadResult.task_id);
      setStatus('è™•ç†ä¸­...');

      // Start polling
      api.startPolling(
        uploadResult.task_id,
        // onUpdate
        (statusData) => {
          setProgress(statusData.progress);
          setStatus(statusData.message);
        },
        // onComplete
        (statusData) => {
          setProgress(100);
          setStatus('å®Œæˆï¼');
          setResult(statusData);
          setIsProcessing(false);
        },
        // onError
        (err) => {
          setError(err.message);
          setIsProcessing(false);
        }
      );

    } catch (err) {
      setError(err instanceof Error ? err.message : 'æœªçŸ¥éŒ¯èª¤');
      setIsProcessing(false);
    }
  }, [api, checkHealth]);

  // ç²å–è™•ç†å¾ŒéŸ³é »æ•¸æ“šï¼ˆä¸è§¸ç™¼ä¸‹è¼‰ï¼‰
  const getProcessedAudio = useCallback(async (taskId: string) => {
    try {
      const { blob } = await api.downloadResult(taskId);
      return blob;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'ä¸‹è¼‰å¤±æ•—');
      throw err;
    }
  }, [api]);

  // ä¸‹è¼‰è™•ç†å¾ŒéŸ³é »æ–‡ä»¶ï¼ˆè§¸ç™¼ä¸‹è¼‰ï¼‰
  const downloadResult = useCallback(async (taskId: string) => {
    try {
      const { blob, filename } = await api.downloadResult(taskId);
      api.downloadFile(blob, filename);
      return blob;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'ä¸‹è¼‰å¤±æ•—');
      throw err;
    }
  }, [api]);

  const reset = useCallback(() => {
    api.stopPolling();
    setIsProcessing(false);
    setProgress(0);
    setStatus('');
    setResult(null);
    setError(null);
    setCurrentTaskId(null);
    setSelectedFile(null);
    setOriginalAudioBlob(null);
  }, [api]);

  return {
    api,
    processAudio,
    downloadResult,
    getProcessedAudio,
    checkHealth,
    reset,
    isProcessing,
    progress,
    status,
    result,
    error,
    currentTaskId,
    selectedFile,
    setSelectedFile,
    originalAudioBlob,
    connectionStatus,
    healthData
  };
}

