import type { UploadResponse, AudioTask, HealthResponse } from "@shared/schema";

export class AudioSeparationAPI {
  public baseURL: string;
  private currentTaskId: string | null = null;
  private pollingInterval: NodeJS.Timeout | null = null;

  constructor(baseURL = 'https://d5c4ba628261.ngrok-free.app') {
    this.baseURL = baseURL;
  }

  async checkHealth(): Promise<HealthResponse> {
    try {
      const response = await fetch(`${this.baseURL}/api/health`, {
        method: 'GET',
        headers: {
          'ngrok-skip-browser-warning': 'true',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      // ä¸å†è¨˜éŒ„ç‚ºéŒ¯èª¤ï¼Œå¥åº·æª¢æŸ¥å¤±æ•—æ˜¯æ­£å¸¸çš„ï¼ˆngrokå¤±æ•ˆæ™‚ï¼‰
      if (error instanceof TypeError && error.message.includes('Failed to fetch')) {
        throw new Error('ç„¡æ³•é€£æ¥åˆ°APIæœå‹™ï¼Œè«‹æª¢æŸ¥ç¶²å€æ˜¯å¦æ­£ç¢º');
      }
      throw error;
    }
  }

  async uploadAudio(audioFile: File): Promise<UploadResponse> {
    try {
      const formData = new FormData();
      formData.append('audio_file', audioFile);

      const response = await fetch(`${this.baseURL}/api/upload`, {
        method: 'POST',
        headers: {
          'ngrok-skip-browser-warning': 'true',
        },
        body: formData
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ error: `HTTP ${response.status}` }));
        throw new Error(errorData.error || 'ä¸Šå‚³å¤±æ•—');
      }

      const data = await response.json();
      this.currentTaskId = data.task_id;
      return data;
    } catch (error) {
      console.error('Upload failed:', error);
      throw error;
    }
  }

  async getTaskStatus(taskId: string): Promise<AudioTask> {
    try {
      const response = await fetch(`${this.baseURL}/api/status/${taskId}`, {
        headers: {
          'ngrok-skip-browser-warning': 'true',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ error: `HTTP ${response.status}` }));
        throw new Error(errorData.error || 'æŸ¥è©¢å¤±æ•—');
      }

      return await response.json();
    } catch (error) {
      console.error('Status check failed:', error);
      throw error;
    }
  }

  async downloadResult(taskId: string): Promise<{ blob: Blob; filename: string }> {
    try {
      const response = await fetch(`${this.baseURL}/api/download/${taskId}`, {
        headers: {
          'ngrok-skip-browser-warning': 'true',
        },
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ error: `HTTP ${response.status}` }));
        throw new Error(errorData.error || 'ä¸‹è¼‰å¤±æ•—');
      }

      const blob = await response.blob();
      const filename = this.getFilenameFromResponse(response) || 'separated_audio.wav';

      return { blob, filename };
    } catch (error) {
      console.error('Download failed:', error);
      throw error;
    }
  }

  private getFilenameFromResponse(response: Response): string | null {
    const disposition = response.headers.get('Content-Disposition');
    if (disposition) {
      const matches = /filename[^;=\n]*=((['"]).*?\2|[^;\n]*)/.exec(disposition);
      if (matches != null && matches[1]) {
        return matches[1].replace(/['"]/g, '');
      }
    }
    return null;
  }

  startPolling(
    taskId: string,
    onUpdate: (status: AudioTask) => void,
    onComplete: (result: AudioTask) => void,
    onError: (error: Error) => void
  ): void {
    if (this.pollingInterval) {
      clearInterval(this.pollingInterval);
    }

    this.pollingInterval = setInterval(async () => {
      try {
        const status = await this.getTaskStatus(taskId);
        onUpdate(status);

        if (status.status === 'completed') {
          this.stopPolling();
          onComplete(status);
        } else if (status.status === 'failed') {
          this.stopPolling();
          onError(new Error(status.error || 'è™•ç†å¤±æ•—'));
        }
      } catch (error) {
        this.stopPolling();
        onError(error instanceof Error ? error : new Error('Unknown polling error'));
      }
    }, 2000);
  }

  stopPolling(): void {
    if (this.pollingInterval) {
      clearInterval(this.pollingInterval);
      this.pollingInterval = null;
    }
  }

  createAudioPlayer(blob: Blob): HTMLAudioElement {
    const audioUrl = URL.createObjectURL(blob);
    const audio = document.createElement('audio');
    audio.src = audioUrl;
    audio.controls = true;
    audio.className = 'w-full';
    return audio;
  }

  downloadFile(blob: Blob, filename: string): void {
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }
}

