import { useEffect, useRef, useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';

interface WaveformDisplayProps {
  originalAudioBlob: Blob | null;
  processedAudioBlob: Blob | null;
  title?: string;
}

export function WaveformDisplay({ originalAudioBlob, processedAudioBlob, title = "éŸ³é »æ³¢å½¢å°æ¯”" }: WaveformDisplayProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const analyzeAudio = async (audioBlob: Blob): Promise<Float32Array> => {
    try {
      const arrayBuffer = await audioBlob.arrayBuffer();
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

      // ç²å–å–®è²é“æ•¸æ“šï¼ˆå¦‚æœæ˜¯ç«‹é«”è²ï¼Œå–å¹³å‡å€¼ï¼‰
      const channelData = audioBuffer.getChannelData(0);
      const length = channelData.length;

      // é™æ¡æ¨£åˆ°åˆé©çš„é»æ•¸ä»¥ä¾¿é¡¯ç¤ºï¼ˆä¾‹å¦‚1000å€‹é»ï¼‰
      const sampleSize = Math.min(1000, length);
      const blockSize = Math.floor(length / sampleSize);
      const waveformData = new Float32Array(sampleSize);

      for (let i = 0; i < sampleSize; i++) {
        let sum = 0;
        for (let j = 0; j < blockSize; j++) {
          sum += Math.abs(channelData[i * blockSize + j] || 0);
        }
        waveformData[i] = sum / blockSize;
      }

      audioContext.close();
      return waveformData;
    } catch (err) {
      console.error('Audio analysis error:', err);
      throw new Error('éŸ³é »åˆ†æå¤±æ•—');
    }
  };

  const drawWaveform = (
    canvas: HTMLCanvasElement,
    originalData: Float32Array | null,
    processedData: Float32Array | null
  ) => {
    const ctx = canvas.getContext('2d');
    if (!ctx) {
      console.error('Cannot get canvas context');
      return;
    }

    const width = canvas.width;
    const height = canvas.height;

    console.log('Canvas dimensions:', width, 'x', height);

    // æ¸…ç©ºç•«å¸ƒ
    ctx.clearRect(0, 0, width, height);

    // èƒŒæ™¯
    ctx.fillStyle = '#f1f5f9';
    ctx.fillRect(0, 0, width, height);

    // ç¶²æ ¼ç·š
    ctx.strokeStyle = '#e2e8f0';
    ctx.lineWidth = 0.5;
    ctx.beginPath();
    // æ°´å¹³ä¸­ç·š
    ctx.moveTo(0, height / 2);
    ctx.lineTo(width, height / 2);
    // å‚ç›´åƒè€ƒç·š
    for (let i = 1; i < 10; i++) {
      const x = (width / 10) * i;
      ctx.moveTo(x, 10);
      ctx.lineTo(x, height - 10);
    }
    ctx.stroke();

    const drawData = (data: Float32Array, color: string, lineWidth: number = 2, alpha: number = 1) => {
      if (!data || data.length === 0) {
        console.log('No data to draw');
        return;
      }

      console.log(`Drawing ${color} waveform with ${data.length} points`);

      ctx.strokeStyle = color;
      ctx.globalAlpha = alpha;
      ctx.lineWidth = lineWidth;
      ctx.beginPath();

      let maxAmplitude = 0;
      for (let i = 0; i < data.length; i++) {
        if (data[i] > maxAmplitude) {
          maxAmplitude = data[i];
        }
      }

      console.log(`Max amplitude: ${maxAmplitude}`);
      const scale = maxAmplitude > 0 ? (height / 4) / maxAmplitude : 1; // ä½¿ç”¨1/4é«˜åº¦ä½œç‚ºç¸®æ”¾

      // ç¹ªè£½ä¸ŠåŠéƒ¨åˆ†
      let hasMovedTo = false;
      for (let i = 0; i < data.length; i++) {
        const x = (i / (data.length - 1)) * width;
        const y = height / 2 - (data[i] * scale);

        if (!hasMovedTo) {
          ctx.moveTo(x, y);
          hasMovedTo = true;
        } else {
          ctx.lineTo(x, y);
        }
      }
      ctx.stroke();

      // ç¹ªè£½ä¸‹åŠéƒ¨åˆ†ï¼ˆé¡åƒï¼‰
      ctx.beginPath();
      hasMovedTo = false;
      for (let i = 0; i < data.length; i++) {
        const x = (i / (data.length - 1)) * width;
        const y = height / 2 + (data[i] * scale);

        if (!hasMovedTo) {
          ctx.moveTo(x, y);
          hasMovedTo = true;
        } else {
          ctx.lineTo(x, y);
        }
      }
      ctx.stroke();
    };

    // ç¹ªè£½åŸå§‹éŸ³é »æ³¢å½¢ï¼ˆè—è‰²ï¼Œè¼ƒæ·¡ï¼‰
    if (originalData) {
      drawData(originalData, '#3b82f6', 1.5, 0.6);
    }

    // ç¹ªè£½è™•ç†å¾ŒéŸ³é »æ³¢å½¢ï¼ˆç¶ è‰²ï¼Œè¼ƒäº®ï¼‰
    if (processedData) {
      drawData(processedData, '#10b981', 2, 0.9);
    }

    // å¦‚æœæ²’æœ‰æ•¸æ“šï¼Œç¹ªè£½ç¤ºä¾‹æ³¢å½¢
    if (!originalData && !processedData) {
      console.log('No data available, drawing sample waveform');
      ctx.strokeStyle = '#94a3b8';
      ctx.lineWidth = 1;
      ctx.globalAlpha = 0.5;
      ctx.beginPath();
      for (let i = 0; i < width; i++) {
        const y = height / 2 + Math.sin((i / width) * Math.PI * 8) * 20;
        if (i === 0) {
          ctx.moveTo(i, y);
        } else {
          ctx.lineTo(i, y);
        }
      }
      ctx.stroke();
    }

    ctx.globalAlpha = 1;
  };

  useEffect(() => {
    const generateWaveforms = async () => {
      const canvas = canvasRef.current;
      if (!canvas) return;

      setIsLoading(true);
      setError(null);

      try {
        // å…ˆè¨­å®šCanvaså°ºå¯¸
        const rect = canvas.getBoundingClientRect();
        const width = rect.width || 800;
        const height = rect.height || 128;

        canvas.width = width;
        canvas.height = height;

        let originalData: Float32Array | null = null;
        let processedData: Float32Array | null = null;

        if (originalAudioBlob) {
          console.log('Analyzing original audio...');
          originalData = await analyzeAudio(originalAudioBlob);
          console.log('Original data length:', originalData.length);
        }

        if (processedAudioBlob) {
          console.log('Analyzing processed audio...');
          processedData = await analyzeAudio(processedAudioBlob);
          console.log('Processed data length:', processedData.length);
        }

        console.log('Drawing waveform...');
        drawWaveform(canvas, originalData, processedData);

      } catch (err) {
        console.error('Waveform generation error:', err);
        setError(err instanceof Error ? err.message : 'ç”Ÿæˆæ³¢å½¢å¤±æ•—');
      } finally {
        setIsLoading(false);
      }
    };

    // å»¶é²åŸ·è¡Œï¼Œç¢ºä¿DOMå…ƒç´ å·²ç¶“æ¸²æŸ“
    const timer = setTimeout(() => {
      if (originalAudioBlob || processedAudioBlob) {
        generateWaveforms();
      }
    }, 100);

    return () => clearTimeout(timer);
  }, [originalAudioBlob, processedAudioBlob]);

  return (
    <Card className="w-full">
      <CardContent className="p-4">
        <div className="mb-3">
          <h4 className="font-semibold text-foreground flex items-center">
            <i className="fas fa-chart-line text-primary mr-2"></i>
            {title}
          </h4>
          <div className="flex items-center space-x-4 mt-2 text-xs text-muted-foreground">
            <div className="flex items-center space-x-1">
              <div className="w-3 h-0.5 bg-blue-500 opacity-70"></div>
              <span>åŸå§‹éŸ³é »</span>
            </div>
            <div className="flex items-center space-x-1">
              <div className="w-3 h-0.5 bg-green-500"></div>
              <span>è™•ç†å¾ŒéŸ³é »</span>
            </div>
          </div>
        </div>

        <div className="relative w-full h-32 bg-muted rounded-lg overflow-hidden">
          {isLoading && (
            <div className="absolute inset-0 flex items-center justify-center bg-muted/80">
              <div className="flex items-center space-x-2">
                <i className="fas fa-spinner fa-spin text-primary"></i>
                <span className="text-sm text-muted-foreground">åˆ†æéŸ³é »ä¸­...</span>
              </div>
            </div>
          )}

          {error && (
            <div className="absolute inset-0 flex items-center justify-center bg-red-50">
              <div className="flex items-center space-x-2 text-red-600">
                <i className="fas fa-exclamation-triangle"></i>
                <span className="text-sm">{error}</span>
              </div>
            </div>
          )}

          <canvas
            ref={canvasRef}
            className="w-full h-full"
            style={{ display: isLoading || error ? 'none' : 'block' }}
          />

          {!isLoading && !error && !originalAudioBlob && !processedAudioBlob && (
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center text-muted-foreground">
                <i className="fas fa-volume-mute text-2xl mb-2 block"></i>
                <span className="text-sm">ç­‰å¾…éŸ³é »è¼‰å…¥...</span>
              </div>
            </div>
          )}
        </div>

        {!isLoading && !error && (originalAudioBlob || processedAudioBlob) && (
          <div className="mt-3 text-xs text-muted-foreground">
            <i className="fas fa-info-circle mr-1"></i>
            æ³¢å½¢åœ–é¡¯ç¤ºéŸ³é »ä¿¡è™Ÿçš„æŒ¯å¹…è®ŠåŒ–ï¼Œç¶ è‰²ç·šæ¢ä»£è¡¨AIè™•ç†å¾Œçš„å¢å¼·æ•ˆæœ
          </div>
        )}
      </CardContent>
    </Card>
  );
}

